import React, {useState, useEffect} from 'react'
import { Text, StyleSheet, TouchableOpacity,  Modal, View } from 'react-native'
//import Modal from 'react-native-modal'

const Definition = ({wordDefinition}) =>{
    return (
        <View>
            {wordDefinition.meanings.map((wordDef)=>{
                console.log(wordDef.definitions)
                return (
                    <View>
                        <Text>{wordDef[0]}</Text>
                        <Text>{wordDef[0]}</Text>
                    </View>
                )
            })}
        </View>
    )
}



const WordOftheDay = () => {

    const [word, setWord] = useState(null)
    const [isVisible, setIsVisible] = useState(false)

    function fetchWord (){
        fetch('https://random-word-api.herokuapp.com/word?number=1')
        .then(response => response.json())
        .then(data =>data[0])
        .then(word => fetchDefinition('funk'))
    }

    function fetchDefinition (word){
        fetch(`https://api.dictionaryapi.dev/api/v2/entries/en_US/${word}`)
        .then(response => response.json())
        .then(data => setWord(data))
    }

    useEffect(() => {
        fetchWord()
    }, [])


    return (
        <TouchableOpacity style={styles.container} onPress={()=>{setIsVisible(true)}}>
            <Modal animationType="fade" visible={isVisible} swipeDirection="down" onSwipeComplete={(e) => { setIsVisible(false) }} presentationStyle='pageSheet'>
                <TouchableOpacity style={{backgroundColor: '#ff5c40', margin: 10, width: 30, height: 30, borderRadius: 15, alignItems: 'center', justifyContent: 'center', shadowOffset: {
        width: 5,
        height: 5,
        },
        shadowRadius: 12,
        shadowOpacity: 0.25,
    borderRadius: 30,}} onPress={()=>setIsVisible(false)}>
                    <Text style={{textAlign: 'center'}}>X</Text>
                </TouchableOpacity>
                <Text>{ word && word[0].word}</Text> 
                {word &&
                    word.map((wordDefinition, index)=>{
                        return <Definition wordDefinition = {wordDefinition} />
                    })
                }

            </Modal> 
            <Text style={{fontWeight: 'bold', fontSize:40,}}>Word of the Day</Text>
            <Text style={{fontSize: 30, padding: 20, fontStyle: 'italic', color: '#ffbe30'}}>{word && word[0].word}</Text>
            <Text style={{fontSize: 17, color: '#3daeff', padding: 0, fontStyle: 'italic', textDecorationLine: 'underline'}}>Learn more</Text>

        </TouchableOpacity>
    )
}

export default WordOftheDay

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: 'white',
        borderRadius: 30,
        marginTop: 40,
        margin: 10,
        shadowOffset: {
            width: 1,
            height: 1
          },
          shadowOpacity: 0.8,
          shadowRadius: 3,
        padding: 20,
    }
})
